import axios from "axios";
import { updateStart, updateSuccess, updateFailure, updateDelete } from "./userSlice";

export const updateUser = async (userInfo, dispatch) => {
     dispatch(updateStart());
     try{
       const {data} = await axios.post("http://localhost:8800/api/users/123/update", userInfo);
       dispatch(updateSuccess(data));
     }
     catch(err){
       dispatch(updateFailure());
     }

};

export const removeUser = async (dispatch) =>{
  try{
    await dispatch(updateDelete());
  }
  catch(err){
    dispatch(updateFailure())
  }
}
